import { Button, Fade, makeStyles, Modal, TextField } from '@material-ui/core';
import Backdrop from '@material-ui/core/Backdrop';
import axios from 'axios';
import { useForm } from "react-hook-form";
import React, { useState } from 'react';

const useStyles = makeStyles((theme) => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: '#fff',
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));

const AddTicker = ({ open, handleClose, children }) => {
    const classes = useStyles();
    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = (data) => {
      console.log("data", data);
    };

    return (
        <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        open={open}
        className={classes.modal}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <h2 id="transition-modal-title">Add a stock</h2>
            <TextField inputRef={register} label="Ticker Symbol" name="ticker" />
            <TextField inputRef={register} label="Number of Stocks" name="units" />
            <TextField inputRef={register} label="Purchase value" name="purchaseValue" />
          </div>
          <Button onClick={handleSubmit(onSubmit)}>Save</Button>
        </Fade>
      </Modal>)
};

export default AddTicker;
